// Componente para visualizar, agregar, editar y eliminar tipos de tarea en el Home
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TaskTypeService } from '../../services/taskType.service';
import { TaskTypeService } from '../../services/taskType.service';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule],
  templateUrl: './home.component.html',
  styleUrls: []
})
export class HomeComponent {
  taskTypes: string[] = [];
  isModalOpen = false;
  newTaskType = '';

  constructor(
    private taskTypeService: TaskTypeService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadTaskTypes();
  }

  loadTaskTypes(): void {
    this.taskTypeService.getTaskTypes().subscribe((types: string[]) => {
      this.taskTypes = types;
    });
  }

  openModal(): void {
    this.isModalOpen = true;
  }

  closeModal(): void {
    this.isModalOpen = false;
    this.newTaskType = '';
  }

  addTaskType(): void {
    if (this.newTaskType.trim()) {
      this.taskTypeService.addTaskType(this.newTaskType).then(() => {
        this.loadTaskTypes();
        this.closeModal();
      });
    }
  }

  enterTaskType(type: string): void {
    this.router.navigate(['/app/category', type]);
  }

  editTaskType(type: string): void {
    const newName = prompt('Editar nombre del tipo de tarea:', type);
    if (newName && newName.trim()) {
      this.taskTypeService.editTaskType(type, newName).then(() => {
        this.loadTaskTypes();
      });
    }
  }

  deleteTaskType(type: string): void {
    if (confirm(`¿Deseas eliminar el tipo de tarea "${type}"?`)) {
      this.taskTypeService.deleteTaskType(type).then(() => {
        this.loadTaskTypes();
      });
    }
  }
}
