import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { CategoryService } from '../../services/category.service';
import { TaskService } from '../../services/task.service';
import { Task } from '../../models/task.model';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule], // 👈 AÑADE ESTO
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {
  categorias: string[] = [];
  newCategory: string = '';
  isModalOpen: boolean = false;

  tareasDelDia: Task[] = [];

  constructor(
    private categoryService: CategoryService,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.loadCategories();
    this.loadTareasDelDia();
  }

  loadCategories(): void {
    this.categoryService.getCategories().subscribe(data => {
      this.categorias = data;
    });
  }

  openModal(): void {
    this.newCategory = '';
    this.isModalOpen = true;
  }

  closeModal(): void {
    this.isModalOpen = false;
  }

  addCategory(): void {
    if (!this.newCategory.trim()) return;

    this.categoryService.addCategory(this.newCategory.trim()).subscribe(() => {
      this.loadCategories();
      this.closeModal();
    });
  }

  loadTareasDelDia(): void {
    this.categoryService.getCategories().subscribe(categorias => {
      categorias.forEach(categoria => {
        this.taskService.getTasks(categoria).subscribe(tareas => {
          tareas
            .filter(t => this.isToday(t.fecha))
            .forEach(t => this.tareasDelDia.push({ ...t, categoria }));
        });
      });
    });
  }

  isToday(fecha: string): boolean {
    if (!fecha || fecha === 'día') return false;
    const today = new Date().toISOString().split('T')[0];
    return fecha === today;
  }
}
<!-- Encabezado principal -->
<div class="flex items-center justify-between p-6 bg-gradient-to-r from-indigo-700 to-indigo-500 text-white rounded-t-xl shadow-md">
  <div>
    <h6 class="text-xl font-bold tracking-wide">Tus Categorías de Tareas</h6>
    <p class="text-sm">Selecciona una categoría para ver o agregar tareas.</p>
  </div>
  <button (click)="openModal()" class="bg-white text-indigo-700 font-semibold px-4 py-2 rounded-md shadow-md hover:bg-indigo-100 transition">
    Agregar Categoría +
  </button>
</div>

<!-- Lista de Categorías -->
<div class="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-4 p-6">
  <div *ngFor="let categoria of categorias" class="bg-white rounded-lg shadow-md p-4 hover:bg-indigo-50 transition cursor-pointer"
       [routerLink]="['/app/category', categoria]">
    <h3 class="text-lg font-bold text-indigo-700">{{ categoria }}</h3>
  </div>
</div>

<!-- Modal Agregar Categoría -->
<div *ngIf="isModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Nueva Categoría</h2>

    <label class="block mb-2">Nombre:</label>
    <input type="text" [(ngModel)]="newCategory" class="w-full p-2 border rounded mb-4" />

    <div class="flex justify-end gap-2">
      <button (click)="closeModal()" class="px-4 py-2 border rounded-md">Cancelar</button>
      <button (click)="addCategory()" class="px-4 py-2 bg-green-500 text-white rounded-md">Agregar</button>
    </div>
  </div>
</div>

<!-- Resumen de tareas del día -->
<div class="p-6 mt-6 bg-blue-50 border rounded-md shadow-sm">
  <h3 class="text-lg font-bold text-blue-800 mb-2">Tareas del Día</h3>
  <table class="w-full min-w-[640px] border-collapse text-sm">
    <thead class="bg-blue-100">
      <tr>
        <th class="text-left py-2 px-4">Categoría</th>
        <th class="text-left py-2 px-4">Nombre</th>
        <th class="text-left py-2 px-4">Nota</th>
        <th class="text-center py-2 px-4">Estado</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let tarea of tareasDelDia" class="border-t">
        <td class="py-2 px-4">{{ tarea.categoria }}</td>
        <td class="py-2 px-4">{{ tarea.nombre }}</td>
        <td class="py-2 px-4">{{ tarea.nota }}</td>
        <td class="py-2 px-4 text-center">{{ tarea.estado }}</td>
      </tr>
    </tbody>
  </table>
</div>
