// Componente para visualizar, agregar, editar y eliminar tipos de tarea en la vista principal
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { TaskTypeService } from '../../services/taskType.service';
import { FormsModule } from '@angular/forms';


@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: []
})
export class HomeComponent implements OnInit {
  taskTypes: string[] = [];
  isModalOpen = false;
  newTaskType = '';

  constructor(
    private taskTypeService: TaskTypeService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.loadTaskTypes();
  }

  // Obtener tipos de tarea desde el servicio
  loadTaskTypes(): void {
    this.taskTypeService.getTaskTypes().subscribe((types: string[]) => {
      this.taskTypes = types;
    });
  }

  // Abrir modal para agregar nuevo tipo
  openModal(): void {
    this.isModalOpen = true;
  }

  // Cerrar modal y limpiar campo
  closeModal(): void {
    this.isModalOpen = false;
    this.newTaskType = '';
  }

  // Agregar nuevo tipo de tarea
  addTaskType(): void {
    if (this.newTaskType.trim()) {
      this.taskTypeService.addTaskType(this.newTaskType).then(() => {
        this.loadTaskTypes();
        this.closeModal();
      });
    }
  }

  // Ir a la vista del tipo seleccionado
  enterTaskType(type: string): void {
    this.router.navigate(['/app/category', type]);
  }

  // Editar nombre del tipo
  editTaskType(type: string): void {
    const newName = prompt('Editar nombre del tipo de tarea:', type);
    if (newName && newName.trim()) {
      this.taskTypeService.editTaskType(type, newName).then(() => {
        this.loadTaskTypes();
      });
    }
  }

  // Eliminar tipo de tarea
  deleteTaskType(type: string): void {
    if (confirm(`Â¿Deseas eliminar el tipo de tarea "${type}"?`)) {
      this.taskTypeService.deleteTaskType(type).then(() => {
        this.loadTaskTypes();
      });
    }
  }
}
<!-- Vista de tipos de tarea en tarjetas -->
<div class="p-6">
  <div class="flex flex-wrap gap-4">
    <div *ngFor="let taskType of taskTypes" class="bg-white rounded-lg shadow-md p-4 w-64 flex flex-col items-center">
      <h3 class="text-lg font-semibold mb-4">{{ taskType }}</h3>
      <div class="flex flex-col gap-2 w-full">
        <button class="bg-teal-600 text-white py-1 rounded hover:bg-teal-700 transition"
                (click)="enterTaskType(taskType)">ENTER</button>
        <div class="flex justify-between px-6">
          <button (click)="editTaskType(taskType)" class="text-blue-600 text-xl">ðŸ–‰</button>
          <button (click)="deleteTaskType(taskType)" class="text-orange-600 text-xl">ðŸ”—</button>
        </div>
      </div>
    </div>

    <!-- BotÃ³n para agregar nuevo tipo de tarea -->
    <div class="bg-green-500 text-white w-64 h-[180px] rounded-lg flex items-center justify-center cursor-pointer text-4xl"
         (click)="openModal()">
      +
    </div>
  </div>
</div>

<!-- Modal para nuevo tipo de tarea -->
<div *ngIf="isModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">New Task Type</h2>
    <label class="block mb-2">Name:</label>
    <input type="text" [(ngModel)]="newTaskType" class="w-full p-2 border rounded mb-4" />
    <div class="flex justify-end gap-2">
      <button (click)="closeModal()" class="px-4 py-2 border rounded-md">Cancel</button>
      <button (click)="addTaskType()" class="px-4 py-2 bg-green-500 text-white rounded-md">Add</button>
    </div>
  </div>
</div>
