// Servicio para gestionar tipos de tareas (categorías) desde Firebase sin usar initializeApp
import { Injectable } from '@angular/core';
import { AuthService } from './auth.service';
import { DateService } from './date.service';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class TaskTypeService {
  private dbUrl = 'https://misdeberes-fac01-default-rtdb.firebaseio.com';

  constructor(
    private authService: AuthService,
    private dateService: DateService,
    private http: HttpClient
  ) {}

  private getPath() {
    const user = this.authService.getUser();
    const fecha = this.dateService.setDate();
    const year = fecha.year;
    const month = fecha.month;

    return `${this.dbUrl}/${user.id}/${year}/${month}/categorias`;
  }

  getAllTaskTypes() {
    const url = `${this.getPath()}.json`;
    return this.http.get<{ [key: string]: any }>(url).pipe(
      map(response => response ? Object.keys(response) : [])
    );
  }

  addTaskType(nombre: string) {
    const url = `${this.getPath()}/${nombre}.json`;
    return this.http.put(url, { tareas: {} });
  }

  deleteTaskType(nombre: string) {
    const url = `${this.getPath()}/${nombre}.json`;
    return this.http.delete(url);
  }

  editTaskType(oldName: string, newName: string) {
    const path = this.getPath();
    const oldUrl = `${path}/${oldName}.json`;
    const newUrl = `${path}/${newName}.json`;

    return this.http.get(oldUrl).pipe(
      map(data => {
        return this.http.put(newUrl, data).subscribe(() => {
          this.http.delete(oldUrl).subscribe();
        });
      })
    );
  }
}
