import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { AuthService } from '../../services/auth.service';
import { DateService } from '../../services/date.service';

@Component({
  selector: 'app-login',
  standalone: true,
  imports: [CommonModule, RouterModule, ReactiveFormsModule],
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export default class LoginComponent implements OnInit {
  private fb = inject(FormBuilder);
  private router = inject(Router);
  private authService = inject(AuthService);
  private dateService = inject(DateService);

  loginForm: FormGroup;
  showModal = false;
  showSuccessModal = false;
  errorMessage = '';
  welcomeName: string = '';

  constructor() {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]]
    });
  }

  ngOnInit(): void {}

  isInvalid(field: string): boolean {
    return this.loginForm.controls[field].invalid && this.loginForm.controls[field].touched;
  }

  onSubmit() {
    if (this.loginForm.valid) {
      const { email, password } = this.loginForm.value;

      this.authService.login(email, password).subscribe({
        next: (res) => {
          const uid = res.localId;

          this.authService.getUserData(uid).subscribe((userData) => {
            const nombre = userData?.nombre || '';
            this.showWelcomeModal(nombre);

            // ✅ Nuevas recomendaciones de ahorro
            const recomendaciones = [
              "Reservá al menos el 10% de tus ingresos como ahorro.",
              "Evitá gastos pequeños repetitivos, pueden sumar mucho.",
              "Asigná metas a tus ahorros: eso te motiva más."
            ];

            this.authService.getUserNotifications(uid).subscribe((notifs) => {
              const existentes = notifs ? Object.values(notifs).map((n: any) => n.mensaje) : [];

              recomendaciones.forEach(msg => {
                if (!existentes.includes(msg)) {
                  this.authService.addNotification(uid, msg).subscribe();
                }
              });
            });
          });
        },
        error: (errorMsg) => {
          this.showErrorModal(this.getFirebaseErrorMessage(errorMsg));
        }
      });
    }
  }

  showErrorModal(message: string) {
    this.errorMessage = message;
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
  }

  showWelcomeModal(nombre: string) {
    this.welcomeName = nombre;
    this.showSuccessModal = true;

    this.dateService.resetToCurrentDate();

    setTimeout(() => {
      this.showSuccessModal = false;
      this.router.navigate(['app/home']);
    }, 1000);
  }

  private getFirebaseErrorMessage(code: string): string {
    switch (code) {
      case 'EMAIL_NOT_FOUND':
      case 'INVALID_PASSWORD':
        return 'Correo o contraseña incorrectos.';
      case 'USER_DISABLED':
        return 'Este usuario ha sido deshabilitado.';
      default:
        return 'Ha ocurrido un error inesperado.';
    }
  }
}
<div class="flex justify-center items-center h-screen w-screen bg-gradient-to-r from-purple-900 to-sky-900">
  <div class="bg-white p-10 rounded-lg shadow-lg w-full max-w-md">

    <h2 class="text-center text-3xl font-bold text-gray-700 mb-6">Iniciar Sesión</h2>

    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">

      <!-- Email -->
      <div class="mb-6">
        <label for="email" class="block text-gray-600 text-sm font-semibold">Correo Electrónico</label>
        <input type="email" id="email" formControlName="email"
               class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-blue-300"
               [ngClass]="{'border-red-500': isInvalid('email')}">
        <p *ngIf="isInvalid('email')" class="text-red-500 text-xs mt-1">Correo inválido.</p>
      </div>

      <!-- Contraseña -->
      <div class="mb-6">
        <label for="password" class="block text-gray-600 text-sm font-semibold">Contraseña</label>
        <input type="password" id="password" formControlName="password"
               class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-blue-300"
               [ngClass]="{'border-red-500': isInvalid('password')}">
        <p *ngIf="isInvalid('password')" class="text-red-500 text-xs mt-1">Debe tener al menos 6 caracteres.</p>
      </div>

      <!-- Botón de Iniciar Sesión -->
      <button type="submit" [disabled]="loginForm.invalid"
              class="w-full bg-blue-500 text-white py-3 rounded-lg shadow-md hover:bg-blue-600 transition disabled:opacity-50">
        Iniciar Sesión
      </button>

      <!-- Enlace a Registro -->
      <p class="text-center text-sm text-gray-500 mt-6">
        ¿No tienes cuenta?
        <a routerLink="/register" class="text-blue-500 font-semibold hover:underline">Regístrate</a>
      </p>

    </form>
  </div>
</div>

<!-- 🔹 MODAL DE ERROR -->
<div *ngIf="showModal" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
  <div class="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full relative">
    <div class="flex justify-center mb-4">
      <div class="w-16 h-16 flex items-center justify-center bg-red-100 rounded-full">
        <svg xmlns="http://www.w3.org/2000/svg" class="h-8 w-8 text-red-500" viewBox="0 0 20 20" fill="currentColor">
          <path fill-rule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zm3-10a1 1 0 00-1.414-1.414L10 8.586 8.414 7A1 1 0 007 8.414L8.586 10 7 11.586A1 1 0 108.414 13L10 11.414l1.586 1.586A1 1 0 1013 11.586L11.414 10 13 8.414A1 1 0 0013 8z" clip-rule="evenodd"/>
        </svg>
      </div>
    </div>
    <h3 class="text-xl font-bold text-gray-800 mb-4 text-center">Error en Inicio de Sesión</h3>
    <p class="text-gray-600 text-center">{{ errorMessage }}</p>
    <button (click)="closeModal()" class="mt-4 w-full bg-red-500 text-white py-2 rounded-lg hover:bg-red-600 transition">
      Cerrar
    </button>
  </div>
</div>

<!-- ✅ MODAL DE BIENVENIDA -->
<div *ngIf="showSuccessModal" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
  <div class="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full relative">
    <div class="flex justify-center mb-4">
      <div class="w-16 h-16 flex items-center justify-center bg-green-100 rounded-full">
        <svg xmlns="http://www.w3.org/2000/svg" class="h-8 w-8 text-green-500" viewBox="0 0 20 20" fill="currentColor">
          <path fill-rule="evenodd" d="M10 18a8 8 0 100-16 8 8 000 16zm3-10l-4 4-2-2a1 1 0 00-1.414 1.414L10 14l5-5a1 1 0 00-1.414-1.414z" clip-rule="evenodd"/>
        </svg>
      </div>
    </div>
    <h3 class="text-xl font-bold text-gray-800 mb-4 text-center">¡Bienvenido, {{ welcomeName }}!</h3>
  </div>
</div>
