import { Component, OnInit, inject } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators, ReactiveFormsModule } from '@angular/forms';
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-register',
  standalone: true,
  imports: [CommonModule, RouterModule, ReactiveFormsModule],
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export default class RegisterComponent implements OnInit {
  private fb = inject(FormBuilder);
  private router = inject(Router);
  private authService = inject(AuthService);

  registerForm: FormGroup;
  errorMessage = '';
  showModal = false;
  showSuccessModal = false;

  constructor() {
    this.registerForm = this.fb.group({
      name: ['', [Validators.required, Validators.minLength(3)]],
      email: ['', [Validators.required, Validators.email]],
      password: ['', [Validators.required, Validators.minLength(6)]],
      confirmPassword: ['', [Validators.required]]
    }, { validator: this.passwordsMatch });
  }

  ngOnInit(): void {}

  isInvalid(field: string): boolean {
    return this.registerForm.controls[field].invalid && this.registerForm.controls[field].touched;
  }

  private passwordsMatch(formGroup: FormGroup) {
    return formGroup.get('password')?.value === formGroup.get('confirmPassword')?.value
      ? null
      : { mismatch: true };
  }

  onSubmit() {
    if (this.registerForm.valid) {
      const { name, email, password } = this.registerForm.value;

      this.authService.register(email, password).subscribe({
        next: (res) => {
          const uid = res.localId;
          this.authService.saveUserProfile(uid, name, email).subscribe({
            next: () => {
              this.showSuccessModal = true;
              setTimeout(() => {
                this.showSuccessModal = false;
                this.router.navigate(['/login']);
              }, 1500);
            },
            error: () => {
              this.showErrorModal('Error al guardar el nombre.');
            }
          });
        },
        error: (err) => {
          this.showErrorModal(this.getFirebaseErrorMessage(err));
        }
      });
    }
  }

  showErrorModal(message: string) {
    this.errorMessage = message;
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
  }

  private getFirebaseErrorMessage(code: string): string {
    switch (code) {
      case 'EMAIL_EXISTS':
        return 'Este correo ya está registrado.';
      default:
        return 'Ha ocurrido un error inesperado.';
    }
  }
}
<div class="flex justify-center items-center h-screen w-screen bg-gradient-to-r from-cyan-800 to-green-600">
  <div class="bg-white p-10 rounded-lg shadow-lg w-full max-w-md">

    <h2 class="text-center text-3xl font-bold text-gray-700 mb-6">Crear Cuenta</h2>

    <!-- Formulario de Registro -->
    <form [formGroup]="registerForm" (ngSubmit)="onSubmit()">

      <!-- Nombre -->
      <div class="mb-6">
        <label for="name" class="block text-gray-600 text-sm font-semibold">Nombre</label>
        <input type="text" id="name" formControlName="name"
          class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-green-300"
          [ngClass]="{'border-red-500': isInvalid('name')}">
        <p *ngIf="isInvalid('name')" class="text-red-500 text-xs mt-1">Debe tener al menos 3 caracteres.</p>
      </div>

      <!-- Email -->
      <div class="mb-6">
        <label for="email" class="block text-gray-600 text-sm font-semibold">Correo Electrónico</label>
        <input type="email" id="email" formControlName="email"
          class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-green-300"
          [ngClass]="{'border-red-500': isInvalid('email')}">
        <p *ngIf="isInvalid('email')" class="text-red-500 text-xs mt-1">Correo inválido.</p>
      </div>

      <!-- Contraseña -->
      <div class="mb-6">
        <label for="password" class="block text-gray-600 text-sm font-semibold">Contraseña</label>
        <input type="password" id="password" formControlName="password"
          class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-green-300"
          [ngClass]="{'border-red-500': isInvalid('password')}">
        <p *ngIf="isInvalid('password')" class="text-red-500 text-xs mt-1">Debe tener al menos 6 caracteres.</p>
      </div>

      <!-- Confirmar Contraseña -->
      <div class="mb-6">
        <label for="confirmPassword" class="block text-gray-600 text-sm font-semibold">Confirmar Contraseña</label>
        <input type="password" id="confirmPassword" formControlName="confirmPassword"
          class="w-full px-4 py-3 mt-2 border rounded-lg focus:ring focus:ring-green-300"
          [ngClass]="{'border-red-500': isInvalid('confirmPassword') || registerForm.hasError('mismatch')}">
        <p *ngIf="registerForm.hasError('mismatch')" class="text-red-500 text-xs mt-1">Las contraseñas no coinciden.</p>
      </div>

      <!-- Botón de Registro -->
      <button type="submit" [disabled]="registerForm.invalid"
        class="w-full bg-green-600 text-white py-3 rounded-lg shadow-md hover:bg-green-700 transition disabled:opacity-50">
        Registrarse
      </button>

      <!-- Enlace a Iniciar Sesión -->
      <p class="text-center text-sm text-gray-500mt-6">
        ¿Ya tienes cuenta?
        <a routerLink="/login" class="text-blue-500 font-semibold hover:underline">Inicia sesión</a>
      </p>

    </form>

  </div>

  <!-- ❌ MODAL DE ERROR -->
  <div *ngIf="showModal" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
    <div class="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full relative">
      <div class="flex justify-center mb-4">
        <div class="w-16 h-16 flex items-center justify-center bg-red-100 rounded-full">
          <svg xmlns="http://www.w3.org/2000/svg" class="h-8 w-8 text-red-500" viewBox="0 0 20 20" fill="currentColor">
            <path fill-rule="evenodd"
              d="M10 18a8 8 0 100-16 8 8 000 16zm3-10a1 1 0 00-1.414-1.414L10 8.586 8.414 7A1 1 0 007 8.414L8.586 10 7 11.586A1 1 0 108.414 13L10 11.414l1.586 1.586A1 1 0 1013 11.586L11.414 10 13 8.414A1 1 0 0013 8z"
              clip-rule="evenodd" />
          </svg>
        </div>
      </div>
      <h3 class="text-xl font-bold text-gray-800 mb-4 text-center">Error al Registrar</h3>
      <p class="text-gray-600 text-center">{{ errorMessage }}</p>
      <button (click)="closeModal()"
        class="mt-4 w-full bg-red-500 text-white py-2 rounded-lg hover:bg-red-600 transition">
        Cerrar
      </button>
    </div>
  </div>

  <!-- ✅ MODAL DE ÉXITO -->
  <div *ngIf="showSuccessModal" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
    <div class="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full relative">
      <div class="flex justify-center mb-4">
        <div class="w-16 h-16 flex items-center justify-center bg-green-100 rounded-full">
          <svg xmlns="http://www.w3.org/2000/svg" class="h-8 w-8 text-green-500" viewBox="0 0 20 20"
            fill="currentColor">
            <path fill-rule="evenodd"
              d="M10 18a8 8 0 100-16 8 8 000 16zm3-10l-4 4-2-2a1 1 0 00-1.414 1.414L10 14l5-5a1 1 0 00-1.414-1.414z"
              clip-rule="evenodd" />
          </svg>
        </div>
      </div>
      <h3 class="text-xl font-bold text-gray-800 mb-4 text-center">¡Registro Exitoso!</h3>
      <p class="text-gray-600 text-center">Redirigiendo al inicio de sesión...</p>
    </div>
  </div>


</div>
