// Servicio para gestionar los tipos de tarea

import { Injectable } from '@angular/core';
import { getDatabase, ref, set, get, remove } from 'firebase/database';
import { AuthService } from './auth.service';
import { DateService } from './date.service';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class TaskTypeService {
  private dbUrl = 'https://misdeberes-fac01-default-rtdb.firebaseio.com';
  private db = getDatabase();

  constructor(
    private authService: AuthService,
    private dateService: DateService,
    private http: HttpClient
  ) {}

  // método para agregar tipo de tarea
  addTaskType(nombre: string): Promise<void> {
    const uid = this.authService.getUid();
    const fecha = new Date();
    const year = fecha.getFullYear();
    const month = ('0' + (fecha.getMonth() + 1)).slice(-2);
    const path = `/${uid}/${year}/${month}/categorias/${nombre}`;
    return set(ref(this.db, path), { tareas: {} });
  }

  // método para editar tipo de tarea
  editTaskType(nombreAntiguo: string, nombreNuevo: string): Promise<void> {
    const uid = this.authService.getUid();
    const fecha = new Date();
    const year = fecha.getFullYear();
    const month = ('0' + (fecha.getMonth() + 1)).slice(-2);
    const basePath = `/${uid}/${year}/${month}/categorias`;

    const oldRef = ref(this.db, `${basePath}/${nombreAntiguo}`);
    const newRef = ref(this.db, `${basePath}/${nombreNuevo}`);

    return get(oldRef).then(snapshot => {
      const data = snapshot.val();
      return set(newRef, data).then(() => remove(oldRef));
    });
  }

  // El método deleteTaskType ya funciona, no se modifica
}
