// Home para visualizar, crear, editar y eliminar tipos de tarea
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { TaskTypeService } from '../../services/taskType.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: [],
  imports: [CommonModule, FormsModule],
})
export class HomeComponent {
  // Lista de tipos de tareas
  taskTypes: string[] = [];

  // Estado del modal para crear tipo
  isModalOpen = false;

  // Nuevo tipo de tarea
  newTaskType = '';

  // Modal lateral: editar
  isEditModalOpen: boolean = false;
  taskTypeToEdit: string = '';
  editedTaskType: string = '';

  // Modal lateral: eliminar
  isDeleteModalOpen: boolean = false;
  taskTypeToDelete: string = '';

  constructor(
    private taskTypeService: TaskTypeService,
    private router: Router
  ) {}

  // Carga inicial de tipos al entrar al componente
  ngOnInit(): void {
    console.log('[INIT] Home cargado');
    this.loadTaskTypes();
  }

  // Consulta todos los tipos de tareas existentes
  loadTaskTypes(): void {
    this.taskTypeService.getAllTaskTypes().subscribe({
      next: (types: string[]) => {
        console.log('[GET] Tipos cargados:', types);
        this.taskTypes = types;
      },
      error: (err) => {
        console.error('[ERROR] Al obtener tipos de tarea:', err);
      },
    });
  }

  // Abre el modal de creación
  openModal(): void {
    this.isModalOpen = true;
  }

  // Cierra el modal de creación
  closeModal(): void {
    this.isModalOpen = false;
    this.newTaskType = '';
  }

  // Agrega un nuevo tipo de tarea
  addTaskType(nombre: string): Observable<any> {
    const path = this.getPath();
    if (!path) return throwError(() => new Error('Ruta no válida'));
  
    const safeName = this.sanitizeKey(nombre);
    const url = `${path}/${safeName}.json`;
    return this.http.put(url, { tareas: {} });
  }

  // Redirige a la vista de categorías según el tipo
  enterTaskType(type: string): void {
    this.router.navigate(['/app/category', type]);
  }

  // ------------------ MODAL DE EDICIÓN ------------------

  // Abre el modal lateral para editar
  openEditModal(type: string): void {
    this.taskTypeToEdit = type;
    this.editedTaskType = type;
    this.isEditModalOpen = true;
  }

  // Cierra el modal lateral de edición
  closeEditModal(): void {
    this.isEditModalOpen = false;
    this.editedTaskType = '';
    this.taskTypeToEdit = '';
  }

  // Guarda los cambios al editar el nombre del tipo
  saveTaskTypeEdit(): void {
    if (this.editedTaskType.trim() && this.taskTypeToEdit) {
      this.taskTypeService
        .editTaskType(this.taskTypeToEdit, this.editedTaskType)
        .subscribe({
          next: () => {
            this.loadTaskTypes();
            this.closeEditModal();
          },
          error: (err) => console.error('[ERROR] Al editar tipo:', err),
        });
    }
    console.log('Guardando:', this.taskTypeToEdit, '=>', this.editedTaskType);
  }

  // ------------------ MODAL DE ELIMINACIÓN ------------------

  // Abre el modal lateral para confirmar eliminación
  openDeleteModal(type: string): void {
    this.taskTypeToDelete = type;
    this.isDeleteModalOpen = true;
  }

  // Cierra el modal de eliminación
  closeDeleteModal(): void {
    this.taskTypeToDelete = '';
    this.isDeleteModalOpen = false;
  }

  // Confirma la eliminación del tipo seleccionado
  confirmDeleteTaskType(): void {
    if (this.taskTypeToDelete) {
      this.taskTypeService.deleteTaskType(this.taskTypeToDelete).subscribe({
        next: () => {
          this.loadTaskTypes();
          this.closeDeleteModal();
        },
        error: (err) => console.error('[ERROR] Al eliminar tipo:', err),
      });
    }
  }

  // Limpia el nombre para que sea una key válida en Firebase
  private sanitizeKey(key: string): string {
    return key.trim().toLowerCase().replace(/\s+/g, '_');
  }
}
<!-- Vista de tipos de tarea -->
<div class="p-6">
  <div class="flex flex-wrap gap-4">
    <div *ngFor="let taskType of taskTypes" class="bg-white rounded-lg shadow-md p-4 w-64 h-[160px] flex flex-col justify-between items-center">
      <h3 class="text-lg font-semibold">{{ taskType }}</h3>

      <!-- Sección inferior con botón e íconos -->
      <div class="flex items-center justify-between w-full gap-2">
        <button class="bg-teal-600 text-white py-1 px-3 rounded hover:bg-teal-700 transition flex-1"
                (click)="enterTaskType(taskType)">INGRESAR</button>
        <button (click)="openEditModal(taskType)" class="text-sky-700 text-xl">
          <span class="material-icons text-sky-700">edit</span>
        </button>
        <button (click)="openDeleteModal(taskType)" class="text-orange-600 text-xl">
          <span class="material-icons text-orange-600">delete</span>
        </button>
      </div>
    </div>

    <!-- Botón para agregar nuevo tipo de tarea -->
    <div class="bg-green-500 text-white w-64 h-[160px] rounded-lg flex items-center justify-center cursor-pointer text-4xl"
         (click)="openModal()">
      +
    </div>
  </div>
</div>

<!-- Modal para nuevo tipo de tarea -->
<div *ngIf="isModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-40">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Nuevo Tipo de Tarea</h2>
    <label class="block mb-2">Nombre:</label>
    <input type="text" [(ngModel)]="newTaskType" class="w-full p-2 border rounded mb-4" />
    <div class="flex justify-end gap-2">
      <button (click)="closeModal()" class="px-4 py-2 border rounded-md">Cancelar</button>
      <button (click)="addTaskType()" class="px-4 py-2 bg-green-500 text-white rounded-md">Agregar</button>
    </div>
  </div>
</div>

<!-- Modal para editar tipo de tarea -->
<div *ngIf="isEditModalOpen" class="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50 z-50">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Editar Tipo de Tarea</h2>
    <p class="text-gray-600 mb-4 text-sm">Modifica el nombre del tipo de tarea.</p>

    <label class="block mb-2 font-semibold">Nuevo Nombre:</label>
    <input type="text" [(ngModel)]="editedTaskType" placeholder="Ej: Universidad"
      class="w-full p-2 border rounded-md mb-4" />

    <div class="flex justify-end gap-2">
      <button (click)="closeEditModal()" class="px-4 py-2 border rounded-md bg-white hover:bg-slate-200">Cancelar</button>
      <button
  (click)="saveTaskTypeEdit()"
  class="px-4 py-2 bg-sky-700 text-white rounded-md hover:bg-sky-800">
