// Servicio para gestionar tipos de tareas desde Firebase sin initializeApp
import { Injectable } from '@angular/core';
import { AuthService } from './auth.service';
import { DateService } from './date.service';
import { HttpClient } from '@angular/common/http';
import { map, switchMap, catchError } from 'rxjs/operators';
import { Observable, of, throwError } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class TaskTypeService {

  // URL base de la base de datos de Firebase
  private dbUrl = 'https://misdeberes-fac01-default-rtdb.firebaseio.com';

  constructor(
    private authService: AuthService,
    private dateService: DateService,
    private http: HttpClient
  ) {}

  // Genera la ruta completa a la ubicación del usuario, año y mes
  private getPath(): string | null {
    const user = this.authService.getUser();
    const year = this.dateService.getSelectedYear();
    const month = this.dateService.getSelectedMonth();

    if (!user?.id || !year || !month) return null;

    return `${this.dbUrl}/${user.id}/${year}/${month}/categorias`;
  }

  // Obtiene todos los nombres de tipos de tarea como array
  getAllTaskTypes(): Observable<string[]> {
    const path = this.getPath();
    if (!path) return of([]);

    const url = `${path}.json`;
    return this.http.get<{ [key: string]: any }>(url).pipe(
      map(response => response ? Object.keys(response) : []),
      catchError(err => {
        console.error('[ERROR][GET TYPES]', err);
        return of([]);
      })
    );
  }

  // Agrega un nuevo tipo de tarea con estructura vacía
  addTaskType(nombre: string): Observable<any> {
    const path = this.getPath();
    if (!path) return throwError(() => new Error('Ruta no válida'));

    const safeName = this.sanitizeKey(nombre);
    const url = `${path}/${safeName}.json`;
    return this.http.put(url, { tareas: {} });
  }

  // Elimina un tipo de tarea por nombre
  deleteTaskType(nombre: string): Observable<any> {
    const path = this.getPath();
    if (!path) return throwError(() => new Error('Ruta no válida'));

    const url = `${path}/${nombre}.json`;
    return this.http.delete(url).pipe(
      catchError(err => {
        console.error('[ERROR][DELETE TYPE]', err);
        return throwError(() => err);
      })
    );
  }

  // Edita el nombre del tipo de tarea (clona y elimina)
  editTaskType(oldName: string, newName: string): Observable<any> {
    const path = this.getPath();
    if (!path) return throwError(() => new Error('Ruta no válida'));

    const oldUrl = `${path}/${oldName}.json`;
    const newUrl = `${path}/${newName}.json`;

    return this.http.get(oldUrl).pipe(
      switchMap(data => this.http.put(newUrl, data)),
      switchMap(() => this.http.delete(oldUrl)),
      catchError(err => {
        console.error('[ERROR][EDIT TYPE]', err);
        return throwError(() => err);
      })
    );
  }
  // Limpia el nombre para que sea una key válida en Firebase
  private sanitizeKey(key: string): string {
    return key.trim().toLowerCase().replace(/\s+/g, '_');
  }
}
