// Home para visualizar, crear, editar y eliminar tipos de tarea
import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { TaskTypeService } from '../../services/taskType.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TaskType } from '../../models/taskType.model'; // Ajusta ruta si cambia

@Component({
  selector: 'app-home',
  standalone: true,
  templateUrl: './home.component.html',
  styleUrls: [],
  imports: [CommonModule, FormsModule],
})
export class HomeComponent {
  // VARIABLES
  newTaskType: string = '';
  editTaskTypeName: string = '';
  originalTaskTypeName: string = '';
  isModalOpen: boolean = false;
  isEditModalOpen: boolean = false;
  taskTypes: string[] = [];

  constructor(
    private taskTypeService: TaskTypeService,
    private router: Router
  ) {}

  ngOnInit(): void {
    console.log('[INIT] Home cargado');
    this.loadTaskTypes();
  }

  loadTaskTypes(): void {
    this.taskTypeService.getAllTaskTypes().subscribe({
      next: (types: string[]) => {
        console.log('[GET] Tipos cargados:', types);
        this.taskTypes = types;
      },
      error: (err) => {
        console.error('[ERROR] Al obtener tipos de tarea:', err);
      },
    });
  }

  openModal(): void {
    this.isModalOpen = true;
  }

  closeModal(): void {
    this.isModalOpen = false;
    this.newTaskType = '';
  }

  // mÃ©todo para agregar tipo de tarea
  addTaskType() {
    if (!this.newTaskType.trim()) return;
    this.taskTypeService.addTaskType(this.newTaskType).then(() => {
      this.taskTypes.push(this.newTaskType);
      this.closeModal();
    });
  }

  enterTaskType(type: string): void {
    this.router.navigate(['/app/category', type]);
  }

  // mÃ©todo para abrir modal de editar
  editTaskType(taskType: string) {
    this.originalTaskTypeName = taskType;
    this.editTaskTypeName = taskType;
    this.isEditModalOpen = true;
  }
  // mÃ©todo para confirmar ediciÃ³n
  confirmEditTaskType() {
    if (!this.editTaskTypeName.trim()) return;
    this.taskTypeService
      .editTaskType(this.originalTaskTypeName, this.editTaskTypeName)
      .then(() => {
        const index = this.taskTypes.indexOf(this.originalTaskTypeName);
        if (index !== -1) {
          this.taskTypes[index] = this.editTaskTypeName;
        }
        this.closeEditModal();
      });
  }
  closeEditModal() {
    this.isEditModalOpen = false;
  }
  deleteTaskType(type: string): void {
    if (confirm(`Â¿Deseas eliminar el tipo de tarea "${type}"?`)) {
      this.taskTypeService.deleteTaskType(type).subscribe({
        next: () => this.loadTaskTypes(),
        error: (err) => console.error('[ERROR] Al eliminar tipo:', err),
      });
    }
  }
}
<!-- Vista de tipos de tarea -->
<div class="p-6">
  <div class="flex flex-wrap gap-4">
    <div *ngFor="let taskType of taskTypes" class="bg-white rounded-lg shadow-md p-4 w-64 flex flex-col items-center">
      <h3 class="text-lg font-semibold mb-4">{{ taskType }}</h3>
      <div class="flex flex-col gap-2 w-full">
        <button class="bg-teal-600 text-white py-1 rounded hover:bg-teal-700 transition"
                (click)="enterTaskType(taskType)">INGRESAR</button>
        <div class="flex justify-between px-6">
          <button (click)="editTaskType(taskType)" class="text-blue-600 text-xl">ðŸ–‰</button>
          <button (click)="deleteTaskType(taskType)" class="text-orange-600 text-xl">ðŸ”—</button>
        </div>
      </div>
    </div>

    <!-- BotÃ³n para agregar nuevo tipo de tarea -->
    <div class="bg-green-500 text-white w-64 h-[180px] rounded-lg flex items-center justify-center cursor-pointer text-4xl"
         (click)="openModal()">
      +
    </div>
  </div>
</div>

<!-- Modal para nuevo tipo de tarea -->
<div *ngIf="isModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Nuevo Tipo de Tarea</h2>
    <label class="block mb-2">Nombre:</label>
    <input type="text" [(ngModel)]="newTaskType" class="w-full p-2 border rounded mb-4" />
    <div class="flex justify-end gap-2">
      <button (click)="closeModal()" class="px-4 py-2 border rounded-md">Cancelar</button>
      <button (click)="addTaskType()" class="px-4 py-2 bg-green-500 text-white rounded-md">Agregar</button>
    </div>
  </div>
</div>
