// Componente para mostrar las tareas de un tipo de deber
import { Component } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { TaskService } from '../../services/task.service';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { TaskTypeService } from '../../services/taskType.service';

@Component({
  selector: 'app-category',
  standalone: true,
  templateUrl: './category.component.html',
  styleUrls: [],
  imports: [CommonModule, FormsModule]
})
export class CategoryComponent {

  // Nombre del tipo de tarea actual (clave Firebase)
  taskTypeName: string = '';
  taskTypeDisplayName: string = '';

  // Lista de tareas cargadas
  tasks: {
    nombre: string;
    nota?: string;
    fecha?: string;
    estado: 'realizado' | 'pendiente' | 'no realizado' | 'para hoy';
  }[] = [];

  // Estado para el modal de agregar
  isAddModalOpen: boolean = false;

  // Tarea nueva temporal
  newTask = {
    nombre: '',
    nota: '',
    fecha: '',
    estado: 'pendiente' as 'pendiente' | 'realizado' | 'no realizado' | 'para hoy'
  };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private taskService: TaskService,
    private taskTypeService: TaskTypeService
  ) {}

  // Carga inicial del tipo de tarea y sus tareas asociadas
  ngOnInit(): void {
    this.taskTypeName = this.route.snapshot.paramMap.get('name') || '';
    console.log('[INIT] Tareas para tipo:', this.taskTypeName);
    this.loadTasks();
    this.loadTaskTypeDisplayName();
  }

  // Consulta tareas desde Firebase
  loadTasks(): void {
    this.taskService.getTasks(this.taskTypeName).subscribe({
      next: (tasks) => {
        this.tasks = tasks;
        console.log('[TAREAS]', tasks);
      },
      error: (err) => console.error('[ERROR] Al cargar tareas:', err)
    });
  }

  // Regresa al home
  goBack(): void {
    this.router.navigate(['/app/home']);
  }

  // Cambia estado entre "realizado" y "pendiente"
  toggleTaskStatus(task: any): void {
    task.estado = task.estado === 'realizado' ? 'pendiente' : 'realizado';
  }

  // Abre el modal para nueva tarea
  openAddModal(): void {
    this.isAddModalOpen = true;
    this.newTask = {
      nombre: '',
      nota: '',
      fecha: '',
      estado: 'pendiente'
    };
  }

  // Cierra el modal de nueva tarea
  closeAddModal(): void {
    this.isAddModalOpen = false;
    this.newTask = {
      nombre: '',
      nota: '',
      fecha: '',
      estado: 'pendiente'
    };
  }

  // Agrega una nueva tarea a Firebase
  addTask(): void {
    if (!this.newTask.nombre.trim()) return;

    this.taskService.addTask(this.taskTypeName, this.newTask).subscribe({
      next: () => {
        this.loadTasks();
        this.closeAddModal();
      },
      error: (err) => console.error('[ERROR] Al agregar tarea:', err)
    });
  }

  // Abre modal de edición (por implementar)
  openEditModal(task: any): void {
    console.log('[MODAL] Editar tarea:', task);
  }

  // Abre modal de eliminación (por implementar)
  openDeleteModal(task: any): void {
    console.log('[MODAL] Eliminar tarea:', task);
  }

  //
  loadTaskTypeDisplayName(): void {
    this.taskTypeService.getAllTaskTypes().subscribe({
      next: (types) => {
        const match = types.find(t => t.key === this.taskTypeName);
        this.taskTypeDisplayName = match?.nombre || this.taskTypeName;
      },
      error: (err) => console.error('[ERROR] Al obtener nombre visual:', err)
    });
  }

}
<!-- Contenedor principal -->
<div class="mb-6 grid grid-cols-1 xl:grid-cols-1 gap-6">
  <div class="relative flex flex-col bg-white rounded-xl shadow-lg overflow-hidden border border-gray-200">

    <!-- Header -->
    <div
      class="flex items-center justify-between p-6 bg-gradient-to-r from-sky-800 to-sky-800 text-white rounded-t-xl shadow-md">
      <div>
        <h6 class="text-xl font-bold tracking-wide">Tareas</h6>
        <p class="text-sm flex items-center gap-2 opacity-90">
          <strong>{{ taskTypeName }}</strong>
        </p>
      </div>
      <button (click)="goBack()" class="text-white hover:underline flex items-center gap-1">
        <span class="material-icons">arrow_back</span> Regresar
      </button>
    </div>

    <!-- Tabla de tareas -->
    <div class="overflow-x-auto">
      <table class="w-full min-w-[640px] border-collapse">
        <thead class="bg-gray-100">
          <tr class="border-b">
            <th class="py-4 px-6 text-left text-xs font-semibold uppercase text-gray-600">Nombre</th>
            <th class="py-4 px-6 text-left text-xs font-semibold uppercase text-gray-600">Nota</th>
            <th class="py-4 px-6 text-center text-xs font-semibold uppercase text-gray-600">Fecha</th>
            <th class="py-4 px-6 text-center text-xs font-semibold uppercase text-gray-600">Estado</th>
            <th class="py-4 px-6 text-center text-xs font-semibold uppercase text-gray-600">Acciones</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let task of tasks" class="border-b transition duration-300 hover:bg-gray-50">
            <td class="py-4 px-6 font-semibold">{{ task.nombre }}</td>
            <td class="py-4 px-6 text-gray-700">{{ task.nota || '-' }}</td>
            <td class="py-4 px-6 text-center">
              {{ task.fecha || '(por definir)' }}
            </td>
            <td class="py-4 px-6 text-center">
              <button (click)="toggleTaskStatus(task)"
                class="px-3 py-1 text-xs font-semibold rounded-full transition duration-300"
                [ngClass]="task.estado === 'realizado' ? 'bg-sky-700 text-white hover:bg-slate-700' : 'bg-orange-600 text-white hover:bg-red-600'">
                {{ task.estado }}
              </button>
            </td>
            <!-- Acciones -->
            <td class="py-4 px-6 text-center">
              <button (click)="openEditModal(task)" class="text-sky-700 text-xl">
                <span class="material-icons text-sky-700">edit</span>
              </button>
              <button (click)="openDeleteModal(task)" class="text-orange-600 text-xl">
                <span class="material-icons text-orange-600">delete</span>
              </button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>

    <!-- Botón para agregar tarea -->
    <div class="flex justify-end p-4">
      <button (click)="openAddModal()"
        class="bg-sky-700 text-white px-4 py-2 rounded-md shadow-md hover:bg-slate-700 transition">
        Agregar Tarea +
      </button>
    </div>
  </div>
</div>
<!-- Modal para agregar nueva tarea -->
<div *ngIf="isAddModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40 z-50">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Nueva Tarea</h2>

    <!-- Campo: Nombre -->
    <label class="block mb-1 font-medium">Nombre:</label>
    <input type="text" [(ngModel)]="newTask.nombre" class="w-full p-2 border rounded mb-4" />

    <!-- Campo: Nota -->
    <label class="block mb-1 font-medium">Nota adicional:</label>
    <textarea [(ngModel)]="newTask.nota" class="w-full p-2 border rounded mb-4" rows="2"></textarea>

    <!-- Campo: Fecha -->
    <label class="block mb-1 font-medium">Fecha (día):</label>
    <input type="text" [(ngModel)]="newTask.fecha" placeholder="Ej: 15 o dejar vacío" class="w-full p-2 border rounded mb-4" />

    <!-- Campo: Estado -->
    <label class="block mb-1 font-medium">Estado:</label>
    <select [(ngModel)]="newTask.estado" class="w-full p-2 border rounded mb-4">
      <option value="pendiente">pendiente</option>
      <option value="realizado">realizado</option>
      <option value="no realizado">no realizado</option>
      <option value="para hoy">para hoy</option>
    </select>

    <!-- Acciones -->
    <div class="flex justify-end gap-2">
      <button (click)="closeAddModal()" class="px-4 py-2 border rounded-md hover:bg-slate-200">Cancelar</button>
      <button (click)="addTask()" class="px-4 py-2 bg-green-600 text-white rounded-md hover:bg-green-700">Agregar</button>
    </div>
  </div>
</div>
