import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Task } from '../../models/task.model';
import { TaskService } from '../../services/task.service';
import { CategoryService } from '../../services/category.service';

@Component({
  selector: 'app-tasks',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './tasks.component.html',
  styleUrls: ['./tasks.component.css']
})
export class TasksComponent implements OnInit {
  tareas: Task[] = [];

  constructor(
    private categoryService: CategoryService,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.loadAllTasks();
  }

  loadAllTasks(): void {
    this.categoryService.getCategories().subscribe(categorias => {
      categorias.forEach(categoria => {
        this.taskService.getTasks(categoria).subscribe(tareas => {
          tareas.forEach(t => {
            this.tareas.push({ ...t, categoria });
          });
        });
      });
    });
  }

  isToday(fecha: string): boolean {
    if (!fecha || fecha === 'día') return false;
    const today = new Date().toISOString().split('T')[0];
    return fecha === today;
  }
}
<p>tasks works!</p>
<!-- Encabezado -->
<div class="flex items-center justify-between p-6 bg-gradient-to-r from-purple-700 to-purple-500 text-white rounded-t-xl shadow-md">
  <div>
    <h6 class="text-xl font-bold tracking-wide">Todas las Tareas del Mes</h6>
    <p class="text-sm opacity-90">Resumen general de tus tareas organizadas por categoría.</p>
  </div>
</div>

<!-- Tabla de tareas -->
<div class="overflow-x-auto mt-4">
  <table class="w-full min-w-[640px] border-collapse text-sm">
    <thead class="bg-gray-100">
      <tr>
        <th class="text-left py-2 px-4">Categoría</th>
        <th class="text-left py-2 px-4">Nombre</th>
        <th class="text-left py-2 px-4">Nota</th>
        <th class="text-center py-2 px-4">Fecha</th>
        <th class="text-center py-2 px-4">Estado</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let tarea of tareas" class="border-t"
          [ngClass]="{
            'bg-green-100': tarea.estado === 'Realizado',
            'bg-red-100': tarea.estado === 'No Realizado',
            'bg-blue-100': isToday(tarea.fecha)
          }">
        <td class="py-2 px-4">{{ tarea.categoria }}</td>
        <td class="py-2 px-4">{{ tarea.nombre }}</td>
        <td class="py-2 px-4">{{ tarea.nota }}</td>
        <td class="py-2 px-4 text-center">{{ tarea.fecha || 'Sin definir' }}</td>
        <td class="py-2 px-4 text-center font-semibold">{{ tarea.estado }}</td>
      </tr>
    </tbody>
  </table>
</div>
