// home.component.ts
import { Component, OnInit } from '@angular/core';
import { TaskTypeService } from '../../services/taskType.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  taskTypes: string[] = [];
  isModalOpen: boolean = false;
  isEditMode: boolean = false;
  newTaskType: string = '';
  taskTypeToEdit: string | null = null;

  constructor(private taskTypeService: TaskTypeService) {}

  ngOnInit(): void {
    this.loadTaskTypes();
  }

  // MÃ©todo para cargar los tipos de tarea desde Firebase
  loadTaskTypes(): void {
    this.taskTypeService.getTaskTypes().subscribe((types) => {
      this.taskTypes = types;
    });
  }

  // MÃ©todo para abrir el modal para agregar o editar tipo de tarea
  openModal(editMode: boolean = false, taskType: string | null = null): void {
    this.isModalOpen = true;
    this.isEditMode = editMode;
    if (editMode && taskType) {
      this.newTaskType = taskType;
      this.taskTypeToEdit = taskType;
    } else {
      this.newTaskType = '';
      this.taskTypeToEdit = null;
    }
  }

  // MÃ©todo para cerrar el modal
  closeModal(): void {
    this.isModalOpen = false;
    this.newTaskType = '';
    this.taskTypeToEdit = null;
    this.isEditMode = false;
  }

  // MÃ©todo para agregar o editar tipo de tarea
  addTaskType(): void {
    const name = this.newTaskType.trim();
    if (!name) return;

    if (this.isEditMode && this.taskTypeToEdit) {
      this.taskTypeService.editTaskType(this.taskTypeToEdit, name).then(() => {
        this.loadTaskTypes();
        this.closeModal();
      });
    } else {
      this.taskTypeService.addTaskType(name).then(() => {
        this.loadTaskTypes();
        this.closeModal();
      });
    }
  }

  // MÃ©todo para eliminar tipo de tarea
  deleteTaskType(taskType: string): void {
    this.taskTypeService.deleteTaskType(taskType).then(() => {
      this.loadTaskTypes();
    });
  }

  // MÃ©todo para editar tipo de tarea
  editTaskType(taskType: string): void {
    this.openModal(true, taskType);
  }

  // MÃ©todo para ingresar al tipo de tarea
  enterTaskType(taskType: string): void {
    // LÃ³gica de navegaciÃ³n no modificada
  }
}
