import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { CategoryService } from '../../services/category.service';
import { TaskService } from '../../services/task.service';
import { Task } from '../../models/task.model';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-home',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule], // ðŸ‘ˆ AÃ‘ADE ESTO
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit {
  categorias: string[] = [];
  newCategory: string = '';
  isModalOpen: boolean = false;

  tareasDelDia: Task[] = [];

  constructor(
    private categoryService: CategoryService,
    private taskService: TaskService
  ) {}

  ngOnInit(): void {
    this.loadCategories();
    this.loadTareasDelDia();
  }

  loadCategories(): void {
    this.categoryService.getCategories().subscribe(data => {
      this.categorias = data;
    });
  }

  openModal(): void {
    this.newCategory = '';
    this.isModalOpen = true;
  }

  closeModal(): void {
    this.isModalOpen = false;
  }

  addCategory(): void {
    if (!this.newCategory.trim()) return;

    this.categoryService.addCategory(this.newCategory.trim()).subscribe(() => {
      this.loadCategories();
      this.closeModal();
    });
  }

  loadTareasDelDia(): void {
    this.categoryService.getCategories().subscribe(categorias => {
      categorias.forEach(categoria => {
        this.taskService.getTasks(categoria).subscribe(tareas => {
          tareas
            .filter(t => this.isToday(t.fecha))
            .forEach(t => this.tareasDelDia.push({ ...t, categoria }));
        });
      });
    });
  }

  isToday(fecha: string): boolean {
    if (!fecha || fecha === 'dÃ­a') return false;
    const today = new Date().toISOString().split('T')[0];
    return fecha === today;
  }
}
<!-- Vista de categorÃ­as de tareas en tarjetas -->
<div class="p-6">
  <div class="flex flex-wrap gap-4">
    <div *ngFor="let categoria of categorias" class="bg-white rounded-lg shadow-md p-4 w-64 flex flex-col items-center">
      <h3 class="text-lg font-semibold mb-4">{{ categoria }}</h3>
      <div class="flex flex-col gap-2 w-full">
        <button class="bg-teal-600 text-white py-1 rounded hover:bg-teal-700 transition"
                (click)="ingresarCategoria(categoria)">INGRESAR</button>
        <div class="flex justify-between px-6">
          <button (click)="editarCategoria(categoria)" class="text-blue-600 text-xl">ðŸ–‰</button>
          <button (click)="eliminarCategoria(categoria)" class="text-orange-600 text-xl">ðŸ”—</button>
        </div>
      </div>
    </div>

    <!-- BotÃ³n para agregar categorÃ­a -->
    <div class="bg-green-500 text-white w-64 h-[180px] rounded-lg flex items-center justify-center cursor-pointer text-4xl"
         (click)="openModal()">
      +
    </div>
  </div>
</div>

<!-- Modal para nueva categorÃ­a -->
<div *ngIf="isModalOpen" class="fixed inset-0 flex items-center justify-center bg-black bg-opacity-40">
  <div class="bg-white p-6 rounded-lg shadow-lg w-96">
    <h2 class="text-xl font-bold mb-4">Nueva CategorÃ­a</h2>
    <label class="block mb-2">Nombre:</label>
    <input type="text" [(ngModel)]="newCategory" class="w-full p-2 border rounded mb-4" />
    <div class="flex justify-end gap-2">
      <button (click)="closeModal()" class="px-4 py-2 border rounded-md">Cancelar</button>
      <button (click)="addCategory()" class="px-4 py-2 bg-green-500 text-white rounded-md">Agregar</button>
    </div>
  </div>
</div>
